#include <string>
#include <vector>
#include <unordered_set>
#include <iostream>

using namespace std;

class Solution {
public:
    bool contains(string s, string t){
      for(int i=0;i<t.length();++i){
        bool find=false;
        for(int j=0;j<s.length();++j){
          if(s[j]==t[i]){
            find=true;
            s.erase(s.begin()+j);
            break;
          }
        }
        if(!find) return false;
      }
      return true;

    }
    bool contains_v2(string s, string t){
      unordered_multiset<char> s_set, t_set;
      for(auto c: s){
        s_set.insert(c);
      }
      for(auto c:t){
        t_set.insert(c);
      }
      for(auto c:t){
        if(s_set.count(c)<t_set.count(c)){
          return false;
        }
      }
      return true;
    }
    string minWindow(string s, string t) {
      int l=0, r=0;

      for(int n=t.length();n<=s.length();++n){
        for(int i=0;i<s.length()-n+1;++i){
          // cout <<"i:" << i << " n: " << n << " " << s.substr(i, n) << " " <<contains(s.substr(i, n), t) << endl;
          if(contains_v2(s.substr(i, n), t)){
            // cout <<"i:" << i << " n: " << n << "\n";
            return s.substr(i, n);
          }
        }
      }
      return "";
    }
};


int main(){
  Solution so;
  cout << so.minWindow("ADOBECODEBANC", "ABC") << endl;//BANC
  cout << so.minWindow("a", "a") << endl;// a
  cout << so.minWindow("bbaa", "aba") << endl; // baa
  cout << so.minWindow("",
"");

}